function outp=sampleGen(figName)% Press enter when you are done, to add data sample user right mouse buttonclose all;clcfigName='this is it';global typ x y fig;fig=figure;set(fig,'Name',figName);axis ([-5 5 -5 5]);typ =1;set(fig,'Position',[300 120 700 600]);set(fig,'Resize','off');hpop = uicontrol('Style', 'popup',...       'String', 'Type1|Type2|Type3|Type4',...       'Position', [180 538 100 50],...       'Callback', {@changeType});hedit = uicontrol('Style', 'edit',...       'String', '[-5 5 -5 5]',...       'Position', [470 567 70 20],...       'Callback', {@changeAxis});hlab1=uicontrol('Style', 'text',...       'String', 'Axis',...       'Position', [390 569 50 17]);hold onkey=1;x=[];y=[];class=[];compasswhile (~ isempty(key))            [xt yt key]=ginput(1);      if (key==3)        x=[x xt];        y=[y yt];        class(end+1)=typ;        switch (typ)            case 1                plot(x(end),y(end),'* blue');            case 2                plot(x(end),y(end),'* red');            case 3                plot(x(end),y(end),'* yellow');            case 4                 plot(x(end),y(end),'* black');            otherwise                 disp('Error data type');        end    endendoutp=[x;y;class];function changeType(obj,event)global typtyp=get(obj,'Value');function changeAxis(obj,event)global fig;figure(fig);axis(str2num(get(obj,'String')));